// A $( document ).ready() block.
$(document).ready(function() {

    var base_url = window.location.origin == "http://localhost" ? window.location.origin + "/ThreeTech" : window.location.origin

    function toSlug(str) {
        // Chuyển hết sang chữ thường
        str = str.toLowerCase();     
     
        // xóa dấu
        str = str.replace(/(à|á|ạ|ả|ã|â|ầ|ấ|ậ|ẩ|ẫ|ă|ằ|ắ|ặ|ẳ|ẵ)/g, 'a');
        str = str.replace(/(è|é|ẹ|ẻ|ẽ|ê|ề|ế|ệ|ể|ễ)/g, 'e');
        str = str.replace(/(ì|í|ị|ỉ|ĩ)/g, 'i');
        str = str.replace(/(ò|ó|ọ|ỏ|õ|ô|ồ|ố|ộ|ổ|ỗ|ơ|ờ|ớ|ợ|ở|ỡ)/g, 'o');
        str = str.replace(/(ù|ú|ụ|ủ|ũ|ư|ừ|ứ|ự|ử|ữ)/g, 'u');
        str = str.replace(/(ỳ|ý|ỵ|ỷ|ỹ)/g, 'y');
        str = str.replace(/(đ)/g, 'd');
     
        // Xóa ký tự đặc biệt
        str = str.replace(/([^0-9a-z-\s])/g, '');
     
        // Xóa khoảng trắng thay bằng ký tự -
        str = str.replace(/(\s+)/g, '-');
        
        // Xóa ký tự - liên tiếp
        str = str.replace(/-+/g, '-');
     
        // xóa phần dự - ở đầu
        str = str.replace(/^-+/g, '');
     
        // xóa phần dư - ở cuối
        str = str.replace(/-+$/g, '');
     
        // return
        return str;
    }

    $('.deleteProductAction').click(function (){
        var id = $(this).attr('value')
        var cate = $('.cate').attr('value')
        $("#deleteProduct").attr("href", base_url + "/san-pham/xoa/"+id+"/"+cate)
    })

    $('.auto-url').click(function(){
        var tenSanPham =  $(".tenSanPham").val()
        const duongDan = toSlug(tenSanPham)
        $(".duongDan").val(duongDan)
    })

    var showPass = false;
    $('#show-password').click(function(){
        showPass = !showPass
        if(showPass == true){
            $(this).text("Ẩn Mật Khẩu?")
            $('#password').get(0).type = 'text';
        }else{
            $(this).text("Hiện Mật Khẩu?")
            $('#password').get(0).type = 'password';
        }
    })

});